@Component  @Slf4j  public class SmsSender &#123;      @Autowired      private SmsConfig smsConfig;      @Autowired      private ISmsService smsService;        /**       * 发送验证码短信       *       * @param telephone 手机号       * @param code     手机验证码       */      public boolean sendMsg(String telephone, String code) &#123;          SendSmsRequest request = new SendSmsRequest();            request.setPhoneNumbers(telephone);          request.setSignName(smsConfig.getSignName());          request.setTemplateCode(smsConfig.getTemplateCode());          ObjectMapper mapper = new ObjectMapper();          JsonNode rootNode = mapper.createObjectNode();          // 存入验证码          ((ObjectNode) rootNode).put(&quot;code&quot;, code);            try &#123;              request.setTemplateParam(mapper.writeValueAsString(rootNode));              val sendSmsResponse = smsService.sendSmsRequest(request);              log.info(&quot;短信发送接收：&quot; + JSONUtil.toJsonPrettyStr(sendSmsResponse));              if (sendSmsResponse.getCode() != null &amp;&amp; &quot;OK&quot;.equals(sendSmsResponse.getCode())) &#123;                  return true;              &#125; else &#123;                  throw new LogicException(&quot;验证码发送失败：&quot; + sendSmsResponse.getMessage());              &#125;          &#125; catch (Exception e) &#123;              log.error(&quot;send sms error.&quot; + e.getMessage());              val operLog = OperLog.builder().title(&quot;发送验证码&quot;)                      .businessType(BusinessType.OTHER.ordinal())                      .operParam(JSONUtil.toJsonPrettyStr(request))                      .status(BusinessStatus.FAIL.ordinal())                      .errorMsg(e.getMessage())                      .build();              AsyncManager.me().execute(AsyncFactory.recordOper(operLog));              throw new LogicException(&quot;验证码发送失败&quot;);          &#125;      &#125;  &#125;

