/**   * 这题和翻转放在一起   * 第一思路就是翻转之后比较是否相等(但是是指针引用，翻转之后是不能进行比较的)   * 看了部分讲解，说是把左子，右子拆分成两棵树来看.   * 可以拆分之后翻转一棵树，然后比较是否相等。   *   * @param root 根节点   * @return 结果   */  public static boolean isSymmetric(TreeNode root) &#123;      if (root == null) &#123;          return true;      &#125;      TreeNode right = root.right;      TreeNode left = root.left;        // 左子树翻转      invertTreePreorder(left);      return isEqual(right, left);  &#125;    private static boolean isEqual(TreeNode right, TreeNode left) &#123;      if (right == null &amp;&amp; left == null) &#123;          return true;      &#125;      if ((right == null &amp;&amp; left != null) || (right != null &amp;&amp; left == null)) &#123;          return false;      &#125;      if (right.val != left.val) &#123;          return false;      &#125;        return isEqual(right.right, left.right) &amp;&amp; isEqual(right.left, left.left);  &#125;    private static void invertTreePreorder(TreeNode root) &#123;      if (root == null) &#123;          return;      &#125;      TreeNode temp;      // 指针交换      temp = root.left;      root.left = root.right;      root.right = temp;        if (root.left != null) &#123;          invertTreePreorder(root.left);      &#125;      if (root.right != null) &#123;          invertTreePreorder(root.right);      &#125;  &#125;
