public static TreeNode buildTree(int[] preorder, int[] inorder) &#123;      if (preorder.length == 0) &#123;          return null;      &#125;      TreeNode root = new TreeNode(preorder[0]);      if (preorder.length == 1) &#123;          return root;      &#125;        int indexIn = -1;      for (int i = 0; i &lt; inorder.length; i++) &#123;          if (inorder[i] == preorder[0]) &#123;              indexIn = i;              break;          &#125;      &#125;      // 左子树      int[] leftTreeInorder = Arrays.copyOfRange(inorder, 0, indexIn);      // 右子树      int[] rightTreeInorder = Arrays.copyOfRange(inorder, indexIn + 1, inorder.length);        root.left = buildTree(Arrays.copyOfRange(preorder, 1, leftTreeInorder.length + 1), leftTreeInorder);      root.right = buildTree(Arrays.copyOfRange(preorder, inorder.length - rightTreeInorder.length, inorder.length), rightTreeInorder);      return root;  &#125;
