@Slf4j  @Component  public class OssUtil &#123;      @Autowired      private OSSClient ossClient;        @Autowired      private OssProperties ossProperties;        /**       * 上传文件       * @param folder &quot;需要加/ 如&quot;xxx/&quot;&quot;       * @return 文件地址       */      public String upload(MultipartFile file, String folder) &#123;          String bucketName = ossProperties.getBucket();            // 判断Bucket的存在          if (!ossClient.doesBucketExist(bucketName)) &#123;              // 创建存储空间              Bucket bucket = ossClient.createBucket(bucketName);              bucketName = bucket.getName();          &#125;            String objectName = &quot;&quot;;          try &#123;              // 以输入流的形式上传文件              InputStream is = file.getInputStream();              // 文件名              String originalFilename = file.getOriginalFilename();              if (ObjectUtil.isNull(originalFilename)) &#123;                  throw new LogicException(&quot;文件名称获取失败&quot;);              &#125;              val suffix = originalFilename.substring(originalFilename.lastIndexOf(&quot;.&quot;));              String fileName = UUID.fastUUID().toString() + suffix.toLowerCase();              objectName = folder + DateUtil.format(new Date(), &quot;yy-MM/dd&quot;) + &quot;/&quot; + fileName;                // 文件大小              long fileSize = file.getSize();                // 创建上传Object的Metadata              ObjectMetadata metadata = new ObjectMetadata();              // 上传的文件的长度              metadata.setContentLength(is.available());              // 指定该Object被下载时的网页的缓存行为              metadata.setCacheControl(&quot;no-cache&quot;);              // 指定该Object下设置Header              metadata.setHeader(&quot;Pragma&quot;, &quot;no-cache&quot;);              // 指定该Object被下载时的内容编码格式              metadata.setContentEncoding(&quot;utf-8&quot;);              // 文件的MIME，定义文件的类型及网页编码，决定浏览器将以什么形式、什么编码读取文件。如果用户没有指定则根据Key或文件名的扩展名生成，              // 如果没有扩展名则填默认值application/octet-stream              metadata.setContentType(AliyunOssFileTypeEnum.getText(suffix.toLowerCase()));              // 指定该Object被下载时的名称（指示MINME用户代理如何显示附加的文件，打开或下载，及文件名称）              metadata.setContentDisposition(&quot;filename/filesize=&quot; + originalFilename + &quot;/&quot; + fileSize + &quot;Byte.&quot;);              // 上传文件 (上传文件流的形式)              PutObjectResult putResult = ossClient.putObject(bucketName, objectName, is, metadata);              // 解析结果              String resultStr = putResult.getETag();              log.info(&quot;唯一MD5数字签名:&quot; + resultStr);          &#125; catch (OSSException oe) &#123;              // oe.printStackTrace();              log.error(&quot;上传阿里云OSS服务器异常.&quot; + oe.getMessage(), oe);          &#125; catch (ClientException ce) &#123;              // ce.printStackTrace();              log.error(&quot;阿里云OSS服务连接异常.&quot; + ce.getMessage(), ce);          &#125; catch (IOException e) &#123;              // e.printStackTrace();              log.error(&quot;文件流异常.&quot; + e.getMessage(), e);          &#125;          return &quot;https://&quot; + ossProperties.getBucket() + &quot;.&quot; + ossProperties.getEndpoint() + &quot;/&quot; + objectName;      &#125;        /**       * 上传文件 byte ()       * @param folder &quot;需要加/ 如&quot;xxx/&quot;&quot;       * @return 文件地址       */      public String imgUploadByByte(byte[] bytes, String folder) &#123;          String bucketName = ossProperties.getBucket();            // 判断Bucket的存在          if (!ossClient.doesBucketExist(bucketName)) &#123;              // 创建存储空间              Bucket bucket = ossClient.createBucket(bucketName);              bucketName = bucket.getName();          &#125;            String objectName = &quot;&quot;;          try &#123;              InputStream inputStream = new ByteArrayInputStream(bytes);                val suffix = &quot;jpg&quot;;              String fileName = UUID.fastUUID().toString() +suffix;              objectName = folder + DateUtil.format(new Date(), &quot;yy-MM/dd&quot;) + &quot;/&quot; + fileName;                // 创建上传Object的Metadata              ObjectMetadata metadata = new ObjectMetadata();              // 上传的文件的长度              metadata.setContentLength(inputStream.available());              // 指定该Object被下载时的网页的缓存行为              metadata.setCacheControl(&quot;no-cache&quot;);              // 指定该Object下设置Header              metadata.setHeader(&quot;Pragma&quot;, &quot;no-cache&quot;);              // 指定该Object被下载时的内容编码格式              metadata.setContentEncoding(&quot;utf-8&quot;);              // 文件的MIME，定义文件的类型及网页编码，决定浏览器将以什么形式、什么编码读取文件。如果用户没有指定则根据Key或文件名的扩展名生成，              // 如果没有扩展名则填默认值application/octet-stream              metadata.setContentType(AliyunOssFileTypeEnum.getText(suffix));              // 上传文件 (上传文件流的形式)              PutObjectResult putResult = ossClient.putObject(bucketName, objectName, inputStream, metadata);              // 解析结果              String resultStr = putResult.getETag();              log.info(&quot;唯一MD5数字签名:&quot; + resultStr);          &#125; catch (OSSException oe) &#123;              // oe.printStackTrace();              log.error(&quot;上传阿里云OSS服务器异常.&quot; + oe.getMessage(), oe);          &#125; catch (ClientException ce) &#123;              // ce.printStackTrace();              log.error(&quot;阿里云OSS服务连接异常.&quot; + ce.getMessage(), ce);          &#125; catch (IOException e) &#123;              // e.printStackTrace();              log.error(&quot;文件流异常.&quot; + e.getMessage(), e);          &#125;          return &quot;https://&quot; + ossProperties.getBucket() + &quot;.&quot; + ossProperties.getEndpoint() + &quot;/&quot; + objectName;      &#125;        /**       * 根据objectName删除OSS服务器上的文件       *       * @param objectName     模拟文件夹名 如&quot;qj_nanjing/&quot; + Bucket下的文件的路径名+文件名 如：&quot;upload/cake.jpg&quot;       */       public Boolean deleteFile(String objectName) &#123;          String bucketName = ossProperties.getBucket();          ossClient.deleteObject(bucketName, objectName);          log.info(&quot;删除&quot; + bucketName + &quot;下的文件&quot; + objectName + &quot;成功&quot;);          return true;      &#125;        /**       * 根据url获取objectName       *     * @param url url       */    public String getObjectNameByUrl(String url) &#123;  //        url = url.replace(&quot;https://&quot; + ossProperties.getBucket() + &quot;.&quot; + ossProperties.getEndpoint() + &quot;/&quot;, &quot;&quot;);          int firstIndex = url.indexOf(&quot;/&quot;);          int secondIndex = url.indexOf(&quot;/&quot;, firstIndex + 1);          int thirdIndex = url.indexOf(&quot;/&quot;, secondIndex + 1);          int suffix = url.lastIndexOf(&quot;?&quot;) &gt; 0 ? url.lastIndexOf(&quot;?&quot;) : url.length();          url = url.substring(thirdIndex + 1, suffix);          return url;      &#125;        /**       * 获取上传文件url (非公共读写时)       *     * @param objectName 模拟文件夹名 如&quot;qj_nanjing/&quot; + Bucket下的文件的路径名+文件名 如：&quot;upload/cake.jpg&quot;       * @return url       */    public String getUrl(String objectName) &#123;          //设置URl过期时间为99年：3600L*1000*24*365*99          Date expiration = new Date(new Date().getTime() + 3600L * 1000 * 24 * 365 * 99);          GeneratePresignedUrlRequest generatePresignedUrlRequest = new GeneratePresignedUrlRequest(ossProperties.getBucket(), objectName);          generatePresignedUrlRequest.setExpiration(expiration);          URL url = ossClient.generatePresignedUrl(generatePresignedUrlRequest);          return url.toString();      &#125;&#125;
