@Component  @Slf4j  @EnableConfigurationProperties(MinioProp.class)  public class MinioUtil &#123;      @Resource      private MinioProp prop;        private MinioClient minioClient;        /**       * 创建minioClient       */    @PostConstruct      public void createMinioClient() &#123;          try &#123;              if (null == minioClient) &#123;                  log.info(&quot;minioClient create start&quot;);                  minioClient = MinioClient.builder().endpoint(prop.getEndpoint())                          .credentials(prop.getAccessKey(), prop.getSecretKey())                          .build();                  createBucket();                  log.info(&quot;minioClient create end&quot;);              &#125;          &#125; catch (Exception e) &#123;              log.error(&quot;连接MinIO服务器异常：&quot; + e);          &#125;      &#125;        /**       * 初始化Bucket       *     * @throws Exception 异常       */      private void createBucket()              throws IOException, InvalidKeyException, InvalidResponseException, InsufficientDataException, NoSuchAlgorithmException, ServerException, InternalException, XmlParserException, InvalidBucketNameException, ErrorResponseException, RegionConflictException &#123;          if (!minioClient.bucketExists(BucketExistsArgs.builder().bucket(prop.getBucketName()).build())) &#123;              minioClient.makeBucket(MakeBucketArgs.builder().bucket(prop.getBucketName()).build());          &#125;      &#125;        /**       * 查看存储bucket是否存在       * @return boolean       */    public Boolean bucketExists(String bucketName) &#123;          Boolean found;          try &#123;              found = minioClient.bucketExists(BucketExistsArgs.builder().bucket(bucketName).build());          &#125; catch (Exception e) &#123;              e.printStackTrace();              return false;          &#125;          return found;      &#125;        /**       * 创建存储bucket       * @return Boolean       */        public Boolean makeBucket(String bucketName) &#123;          try &#123;              if (!bucketExists(bucketName)) &#123;                  minioClient.makeBucket(MakeBucketArgs.builder()                          .bucket(bucketName)                          .build());                  String policyJson = &quot;&#123;\n&quot; +                          &quot;\t\&quot;Version\&quot;: \&quot;&quot;+new SimpleDateFormat(&quot;yyyy-mm-dd&quot;).format(System.currentTimeMillis())+&quot;\&quot;,\n&quot; +                          &quot;\t\&quot;Statement\&quot;: [&#123;\n&quot; +                          &quot;\t\t\&quot;Effect\&quot;: \&quot;Allow\&quot;,\n&quot; +                          &quot;\t\t\&quot;Principal\&quot;: &#123;\n&quot; +                          &quot;\t\t\t\&quot;AWS\&quot;: [\&quot;*\&quot;]\n&quot; +                          &quot;\t\t&#125;,\n&quot; +                          &quot;\t\t\&quot;Action\&quot;: [\&quot;s3:GetBucketLocation\&quot;, \&quot;s3:ListBucket\&quot;, \&quot;s3:ListBucketMultipartUploads\&quot;],\n&quot; +                          &quot;\t\t\&quot;Resource\&quot;: [\&quot;arn:aws:s3:::&quot; + bucketName + &quot;\&quot;]\n&quot; +                          &quot;\t&#125;, &#123;\n&quot; +                          &quot;\t\t\&quot;Effect\&quot;: \&quot;Allow\&quot;,\n&quot; +                          &quot;\t\t\&quot;Principal\&quot;: &#123;\n&quot; +                          &quot;\t\t\t\&quot;AWS\&quot;: [\&quot;*\&quot;]\n&quot; +                          &quot;\t\t&#125;,\n&quot; +                          &quot;\t\t\&quot;Action\&quot;: [\&quot;s3:AbortMultipartUpload\&quot;, \&quot;s3:DeleteObject\&quot;, \&quot;s3:GetObject\&quot;, \&quot;s3:ListMultipartUploadParts\&quot;, \&quot;s3:PutObject\&quot;],\n&quot; +                          &quot;\t\t\&quot;Resource\&quot;: [\&quot;arn:aws:s3:::&quot; + bucketName + &quot;/*\&quot;]\n&quot; +                          &quot;\t&#125;]\n&quot; +                          &quot;&#125;\n&quot;;                  minioClient.setBucketPolicy(SetBucketPolicyArgs.builder().bucket(bucketName).config(policyJson).build());                  log.info(&quot;buckets：【&#123;&#125;】,创建[readwrite]策略成功！&quot;, bucketName);              &#125; else &#123;                  log.info(&quot;minio bucket-&gt;&gt;&gt;【&#123;&#125;】already exists&quot;, bucketName);              &#125;          &#125; catch (Exception e) &#123;              e.printStackTrace();              return false;          &#125;          return true;      &#125;      /**       * 删除存储bucket       * @return Boolean       */        public Boolean removeBucket(String bucketName) &#123;          try &#123;              minioClient.removeBucket(RemoveBucketArgs.builder()                      .bucket(bucketName)                      .build());          &#125; catch (Exception e) &#123;              e.printStackTrace();              return false;          &#125;          return true;      &#125;      /**       * 获取全部bucket       */        public List&lt;Bucket&gt; getAllBuckets() &#123;          try &#123;              List&lt;Bucket&gt; buckets = minioClient.listBuckets();              return buckets;          &#125; catch (Exception e) &#123;              e.printStackTrace();          &#125;          return null;      &#125;      /**       * 文件上传       *       * @param file 文件       * @return Boolean       */        public OSSFile upload(MultipartFile file) &#123;          String originalFilename = file.getOriginalFilename();          if (StrUtil.isBlank(originalFilename))&#123;              throw new RuntimeException();          &#125;          // 重命名          String fileName = UUID.fastUUID() + originalFilename.substring(originalFilename.lastIndexOf(&quot;.&quot;));          String objectName = DateUtil.format(new Date(), &quot;yy-MM/dd&quot;) + &quot;/&quot; + fileName;          try &#123;              PutObjectArgs objectArgs = PutObjectArgs.builder().bucket(prop.getBucketName()).object(objectName)                      .stream(file.getInputStream(), file.getSize(), -1).contentType(file.getContentType()).build();              //文件名称相同会覆盖              minioClient.putObject(objectArgs);          &#125; catch (Exception e) &#123;              e.printStackTrace();              return null;          &#125;            OSSFile ossFile = new OSSFile();          ossFile.setFileName(originalFilename);          ossFile.setObjectName(objectName);          ossFile.setUrl(prop.getEndpoint() + &quot;/&quot; + prop.getBucketName() + &quot;/&quot; + objectName);          return ossFile;      &#125;        /**       * 预览图片       * @param fileName       * @return       */      public String preview(String fileName)&#123;          // 查看文件地址          GetPresignedObjectUrlArgs build = new GetPresignedObjectUrlArgs().builder().bucket(prop.getBucketName()).object(fileName).method(Method.GET).build();          try &#123;              String url = minioClient.getPresignedObjectUrl(build);              return url;          &#125; catch (Exception e) &#123;              e.printStackTrace();          &#125;          return null;      &#125;        /**       * 文件下载       * @param fileName 文件名称       * @param res response       * @return Boolean       */        public void download(String fileName, HttpServletResponse res) &#123;          GetObjectArgs objectArgs = GetObjectArgs.builder().bucket(prop.getBucketName())                  .object(fileName).build();          try (InputStream response = minioClient.getObject(objectArgs))&#123;              byte[] buf = new byte[1024];              int len;              try (FastByteArrayOutputStream os = new FastByteArrayOutputStream())&#123;                  while ((len=response.read(buf))!=-1)&#123;                      os.write(buf,0,len);                  &#125;                  os.flush();                  byte[] bytes = os.toByteArray();                  res.setCharacterEncoding(&quot;utf-8&quot;);                  // 设置强制下载不打开                  // res.setContentType(&quot;application/force-download&quot;);                  res.addHeader(&quot;Content-Disposition&quot;, &quot;attachment;fileName=&quot; + fileName);                  try (ServletOutputStream stream = res.getOutputStream())&#123;                      stream.write(bytes);                      stream.flush();                  &#125;              &#125;          &#125; catch (Exception e) &#123;              e.printStackTrace();          &#125;      &#125;        /**       * 查看文件对象       * @return 存储bucket内文件对象信息       */      public List&lt;Item&gt; listObjects() &#123;          Iterable&lt;Result&lt;Item&gt;&gt; results = minioClient.listObjects(                  ListObjectsArgs.builder().bucket(prop.getBucketName()).build());          List&lt;Item&gt; items = new ArrayList&lt;&gt;();          try &#123;              for (Result&lt;Item&gt; result : results) &#123;                  items.add(result.get());              &#125;          &#125; catch (Exception e) &#123;              e.printStackTrace();              return null;          &#125;          return items;      &#125;        /**       * 删除       * @param fileName       * @return       * @throws Exception       */      public boolean remove(String fileName)&#123;          try &#123;              minioClient.removeObject( RemoveObjectArgs.builder().bucket(prop.getBucketName()).object(fileName).build());          &#125;catch (Exception e)&#123;              return false;          &#125;          return true;      &#125;  &#125;
